mapper.select:
SELECT p10_assets.assets_id, p10_assets.assets_name, p10_assets.assets_type, p10_assets.`type`, p10_assets.`owner`, p10_project.project_id, p10_project.company_code, p10_project.`status`, p10_project.subject_persons, p10_assets_re.assets_re_id, p10_assets_re.assets_source, json_extract( p10_assets_re.assets_re_detail, '$.entityZX_STZX.entityZX_STZX_CCZXCF.entityZX_STZX_CCZXCF_CCZXCF.sealUpCondition' ) AS sealUpCondition, json_extract( p10_assets_re.assets_re_detail, '$.fundingZX_ZJZX.fundingZX_ZJZX_ZJZXDJ.fundingZX_ZJZX_ZJZXDJ_ZJZXDJ.whetherFirstFrozen' ) AS whetherFirstFrozen FROM p10_project LEFT JOIN p10_assets_re ON p10_project.project_id = p10_assets_re.project_id and p10_assets_re.del_flag = 0 LEFT JOIN p10_assets ON p10_assets_re.assets_id = p10_assets.assets_id and p10_assets.del_flag = 0 WHERE p10_assets.del_flag = 0
and p10_project.ins_id = #{insId}
and p10_project.outles_id = #{outlesId}
and p10_assets_re.subject_id = #{query.subjectId}
and p10_project.company_code like concat('%', #{query.companyCode}, '%')
and p10_assets.assets_name like concat('%', #{query.assetsName}, '%')
and p10_assets.assets_type = #{query.assetsType}
and p10_assets.account_number like concat(#{query.accountNumber}, '%')
and p10_project.subject_persons like concat('%', #{query.debtorName}, '%')
