mapper.select:
SELECT * FROM p10_assets_re WHERE del_flag = 0

	and assets_re_id = #{query.assetsReId}


	and assets_id = #{query.assetsId}


	and target_id = #{query.targetId}


	and project_id = #{query.projectId}


	and casee_id = #{query.caseeId}


	and create_casee_id = #{query.createCaseeId}


	and subject_id = #{query.subjectId}


	and subject_name like concat('%', #{query.subjectName}, '%')


	and assetsSource = #{query.assetsSource}

select p10_project.company_code,p10_casee.casee_number,p10_assets_re.*,p10_assets.assets_name,p10_assets.type,p10_assets.assets_type,p10_assets.`owner`,p10_assets.account_number,p10_task_node.node_id from p10_assets_re left join p10_assets on p10_assets.assets_id = p10_assets_re.assets_id left join p10_casee on p10_casee.casee_id = p10_assets_re.casee_id left join p10_project on p10_project.project_id = p10_assets_re.project_id left join p10_target on p10_target.goal_id = p10_assets.assets_id and p10_casee.casee_id = p10_target.casee_id left join p10_task_node node on node.target_id = p10_target.target_id left join p10_task_node on p10_task_node.node_id = node.node_id where p10_project.del_flag = 0 and p10_assets_re.casee_id is not null and p10_project.`status` = 1000 and p10_casee.`status` = 1 and p10_target.goal_type = 20001 and p10_task_node.node_attributes = 400 and node.node_key like "%SDQK" and node.form_data is not null and (p10_task_node.node_key = "entityZX_STZX_CCZXCF_CCZXCF" or p10_task_node.node_key = "fundingZX_ZJZX_ZJZXDJ_ZJZXDJ")
and p10_casee.casee_type = #{query.caseeType}
and p10_assets.type = #{query.type}
and json_extract( p10_assets_re.assets_re_detail, '$.assetsFreeze' ) is null
and json_extract( p10_assets_re.assets_re_detail, '$.assetsSeizure' ) is null
and (json_extract( p10_assets_re.assets_re_detail, '$.assetsFreeze' ) is null or json_extract( p10_assets_re.assets_re_detail, '$.assetsSeizure' ) is null)
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_assets.owner like concat('%', #{query.executedName}, '%')
and p10_assets.assets_name like concat('%', #{query.assetsName}, '%')

select p10_project.company_code,p10_casee.casee_number,p10_assets_re.*,p10_assets.assets_name,p10_assets.type,p10_assets.assets_type,p10_assets.`owner`,p10_assets.account_number,p10_task_node.node_id from p10_assets_re left join p10_assets on p10_assets.assets_id = p10_assets_re.assets_id left join p10_casee on p10_casee.casee_id = p10_assets_re.casee_id left join p10_project on p10_project.project_id = p10_assets_re.project_id left join p10_target on p10_target.goal_id = p10_assets.assets_id and p10_casee.casee_id = p10_target.casee_id left join p10_task_node node on node.target_id = p10_target.target_id left join p10_task_node on p10_task_node.node_id = node.node_id where p10_project.del_flag = 0 and p10_assets_re.casee_id is not null and p10_project.`status` = 1000 and p10_assets.type = 20200 and p10_casee.`status` = 1 and p10_target.goal_type = 20001 and p10_casee.casee_type in (3010,3031) and node.node_key = "entityZX_STZX_CCZXSQYS_CCZXSQYS" and node.form_data is null and node.node_attributes = 400 and json_extract( p10_assets_re.assets_re_detail, '$.mortgagee' ) = 0 and json_extract( p10_assets_re.assets_re_detail, '$.assetsSeizure.sealUpCondition' ) = 1
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_assets.owner like concat('%', #{query.executedName}, '%')
and p10_assets.assets_name like concat('%', #{query.assetsName}, '%')

select p10_project.company_code,p10_casee.casee_number,p10_assets_re.*,p10_assets.assets_name,p10_assets.type,p10_assets.assets_type,p10_assets.`owner`,p10_assets.account_number,p10_task_node.node_id from p10_assets_re left join p10_assets on p10_assets.assets_id = p10_assets_re.assets_id left join p10_casee on p10_casee.casee_id = p10_assets_re.casee_id left join p10_project on p10_project.project_id = p10_assets_re.project_id left join p10_target on p10_target.goal_id = p10_assets_re.assets_id and p10_assets_re.casee_id = p10_target.casee_id left join p10_task_node on p10_task_node.target_id = p10_target.target_id where p10_project.del_flag = 0 and p10_assets_re.casee_id is not null and p10_project.`status` = 1000 and p10_assets.type = 20100 and p10_casee.casee_type in (3010,3031) and p10_casee.`status` = 1 and p10_target.goal_type = 20001 and json_extract( p10_assets_re.assets_re_detail, '$.assetsFreeze.whetherFirstFrozen' ) = 1 and json_extract( p10_assets_re.assets_re_detail, '$.assetsSnap' ) is null and p10_task_node.node_attributes = 400 and p10_task_node.node_key = "fundingZX_ZJZX_ZJZXZJHK_ZJZXZJHK"
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_assets.owner like concat('%', #{query.executedName}, '%')
and p10_assets.assets_name like concat('%', #{query.assetsName}, '%')
