AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}

SELECT p10_assets.type, count(0) AS numberOfProperties FROM p10_assets_re LEFT JOIN p10_project ON p10_project.project_id = p10_assets_re.project_id and p10_assets_re.del_flag = 0 LEFT JOIN p10_assets ON p10_assets_re.assets_id = p10_assets.assets_id and p10_assets.del_flag = 0 WHERE p10_project.del_flag = 0
AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}
