mapper.select:
SELECT p10_bank_loan.*, p10_institution.ins_name AS entrustedInsName, p10_outles.outles_name AS entrustedOutlesName, IF ( ISNULL( p10_project.STATUS ), p10_transfer_record.`status`, p10_project.STATUS ) AS STATUS FROM p10_bank_loan LEFT JOIN ( SELECT t.transfer_record_id, t.`status`, t.source_id, t.project_id FROM ( SELECT * FROM p10_transfer_record WHERE del_flag = 0 AND transfer_type = 0 ORDER BY transfer_record_id DESC LIMIT 10000 ) t GROUP BY t.source_id ) p10_transfer_record ON p10_transfer_record.source_id = p10_bank_loan.bank_loan_id LEFT JOIN p10_institution ON p10_institution.ins_id = p10_bank_loan.ins_id LEFT JOIN p10_outles ON p10_outles.outles_id = p10_bank_loan.outles_id LEFT JOIN p10_project ON p10_project.project_id = p10_transfer_record.project_id WHERE p10_bank_loan.del_flag = 0
and p10_bank_loan.ins_id = #{login.insId}
and p10_bank_loan.outles_id = #{login.outlesId}
and p10_institution.ins_name like concat('%', #{query.trustee}, '%')
and p10_bank_loan.subject_name like concat('%', #{query.subjectName}, '%')
and p10_bank_loan.transfer_date = #{query.transferDate}
and p10_transfer_record.status = #{query.status}
and p10_project.status = #{query.status}
ORDER BY p10_bank_loan.create_time desc
