mapper.select:
select p10_project.company_code,p10_casee.casee_number,p10_behavior.*,task_node.node_id from p10_behavior left join p10_project on p10_project.project_id = p10_behavior.project_id left join p10_casee on p10_casee.casee_id = p10_behavior.casee_id left join p10_target on p10_target.goal_id = p10_behavior.behavior_id left join (select * from ( select p10_task_node.target_id,p10_task_node.node_key,p10_task_node.node_id from p10_task_node where p10_task_node.`status` = 0 and p10_task_node.node_attributes = 400 ORDER BY p10_task_node.sort asc limit 999999999999 )task_node GROUP BY task_node.target_id ) task_node on task_node.target_id = p10_target.target_id where p10_project.del_flag = 0 and p10_project.`status` = 1000 and p10_behavior.behavior_status = 1 and p10_target.goal_type = 30001 and task_node.node_id is not null
and task_node.node_key = #{query.nodeKey}
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and p10_behavior.type = #{query.type}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.executed_name like concat('%', #{query.executedName}, '%')
AND p10_behavior.behavior_date BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

select p10_behavior.*,p10_task_node.node_id,casee.* from p10_casee_subject_re left join ( select p10_project.project_id,p10_project.ins_id,p10_project.outles_id,p10_project.company_code,p10_casee.casee_id,p10_casee.casee_number from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id left join p10_project on p10_project.project_id = p10_project_casee_re.project_id where p10_project.del_flag = 0 and p10_casee.casee_type in (3010,3031) and json_extract(p10_project.project_detail,'$.projectAmount') = json_extract(p10_project.project_detail,'$.repaymentAmount') )casee on casee.casee_id = p10_casee_subject_re.casee_id left join p10_behavior on p10_behavior.subject_id = p10_casee_subject_re.subject_id and p10_behavior.casee_id = casee.casee_id left join p10_target on p10_target.goal_id = p10_behavior.behavior_id left join p10_task_node on p10_task_node.target_id = p10_target.target_id where p10_behavior.behavior_id is not null and p10_behavior.behavior_status = 1 and p10_target.goal_type = 30001 and p10_task_node.node_attributes = 400 and p10_task_node.node_key like "%XZCX"
and casee.ins_id = #{login.insId}
and casee.outles_id = #{login.outlesId}
and (casee.company_code like concat('%', #{query.caseeNumber}, '%') or casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.executed_name like concat('%', #{query.executedName}, '%')
AND p10_behavior.behavior_date BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

select p10_behavior.*,p10_project.project_id,p10_project.company_code,p10_project.status projectStatus,p10_casee.casee_id,p10_casee.casee_number,p10_casee.status caseeStatus from p10_behavior left join p10_project on p10_project.project_id = p10_behavior.project_id left join p10_casee on p10_casee.casee_id = p10_behavior.casee_id where p10_project.del_flag = 0
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
order by p10_project.project_id,p10_casee.casee_id asc
