mapper.select:
SELECT p10_behavior.*, p10_project.project_id, p10_project.company_code, p10_project.status, p10_project.subject_persons, p10_casee.casee_number, p10_casee.casee_id FROM p10_behavior LEFT JOIN p10_casee ON p10_behavior.casee_id = p10_casee.casee_id and p10_casee.del_flag = 0 LEFT JOIN p10_project ON p10_behavior.project_id = p10_project.project_id and p10_project.del_flag = 0 WHERE p10_behavior.del_flag = 0 AND p10_behavior.subject_id = #{subjectId}
and p10_project.ins_id = #{insId}
and p10_project.outles_id = #{outlesId}

select * from p10_behavior
where p10_behavior.del_flag = 0

	and project_id = #{projectId}


	and casee_id = #{caseeId}


	and subject_id = #{subjectId}
	and casee_id is not null

SELECT * FROM p10_behavior WHERE del_flag = 0

	and behavior_id = #{query.behaviorId}


	and project_id = #{query.projectId}


	and casee_id = #{query.caseeId}


	and subject_id = #{query.subjectId}


	and executed_namelike like concat('%', #{query.executedName}, '%')


	and 'type' = #{query.type}


	and limit_type = #{query.limitType}


	and behavior_status = #{query.behaviorStatus}
