mapper.select:
select p10_casee.*,p10_court.court_name as courtName,p10_casee_lawyer_re.actual_name as lawyerName,p10_project_casee_re.actual_name as actualName from p10_casee left join p10_court on p10_court.court_id = p10_casee.court_id left join p10_casee_lawyer_re on p10_casee_lawyer_re.casee_id = p10_casee.casee_id left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id where p10_casee.del_flag = 0
and p10_project_casee_re.project_id = #{projectId}
and p10_casee.casee_type in
#{item}
order by p10_casee.casee_type,p10_casee.category,p10_casee.start_time

select p10_casee.* from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id where p10_casee.del_flag = 0
and p10_project_casee_re.project_id = #{projectId}
and p10_casee.status in
#{item}
LIMIT 1

select p10_project.company_code,p10_project.project_id,p10_casee.*,p10_casee_lawyer_re.actual_name as lawyerName,p10_court.court_name,p10_project_casee_re.actual_name as userName from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id left join p10_project on p10_project.project_id = p10_project_casee_re.project_id left join p10_casee_lawyer_re on p10_casee_lawyer_re.casee_id = p10_casee.casee_id left join p10_court on p10_court.court_id = p10_casee.court_id where p10_casee.del_flag = 0
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.executed_name like concat('%', #{query.executedName}, '%')
and p10_project_casee_re.actual_name like concat('%', #{query.actualName}, '%')
and p10_casee.casee_type = #{query.caseeType}
and p10_casee.status = #{query.status}
AND p10_casee.start_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')
ORDER BY p10_casee.status asc,p10_casee.start_time desc

SELECT p10_subject.*, p10_casee_subject_re.type FROM p10_casee_subject_re LEFT JOIN p10_subject ON p10_casee_subject_re.subject_id = p10_subject.subject_id AND p10_subject.del_flag = 0 WHERE p10_casee_subject_re.del_flag = 0 AND p10_casee_subject_re.casee_id = #{query.caseeId}
AND p10_casee_subject_re.casee_personnel_type = #{query.caseePersonnelType}
ORDER BY p10_casee_subject_re.type asc

select p10_subject.* from p10_casee_subject_re
left join p10_subject on p10_subject.subject_id = p10_casee_subject_re.subject_id
where p10_casee_subject_re.del_flag = 0
and p10_casee_subject_re.casee_id = #{query.caseeId}

	and p10_casee_subject_re.type = #{query.type}


	and p10_subject.name like concat('%', #{query.subjectName}, '%')

select p10_subject.*,p10_casee_subject_re.casee_id from p10_casee_subject_re
left join p10_subject on p10_casee_subject_re.subject_id = p10_subject.subject_id
where p10_casee_subject_re.del_flag = 0
and p10_casee_subject_re.casee_id = #{caseeId}

	and p10_casee_subject_re.casee_personnel_type = #{caseePersonnelType}

select p10_project.project_id,p10_project.company_code,p10_casee.*,p10_casee_lawyer_re.actual_name as lawyerName,p10_court.court_name,p10_project_casee_re.actual_name as userName from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id left join p10_project on p10_project.project_id = p10_project_casee_re.project_id left join p10_assets_re on p10_assets_re.casee_id = p10_casee.casee_id left join p10_casee_lawyer_re on p10_casee_lawyer_re.casee_id = p10_casee.casee_id left join p10_court on p10_court.court_id = p10_casee.court_id where p10_casee.del_flag = 0 and p10_assets_re.casee_id is null
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.executed_name like concat('%', #{query.executedName}, '%')
and p10_project_casee_re.actual_name like concat('%', #{query.actualName}, '%')
and p10_casee.casee_type = #{query.caseeType}
and p10_casee.status = #{query.status}
AND p10_casee.start_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')
ORDER BY p10_casee.status asc,p10_casee.start_time desc

select p10_project.company_code,p10_casee.*,task_node.node_id,p10_project.project_id,p10_casee_lawyer_re.actual_name as lawyerName,p10_court.court_name,p10_project_casee_re.actual_name as userName from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id left join p10_project on p10_project.project_id = p10_project_casee_re.project_id left join p10_target on p10_target.casee_id = p10_casee.casee_id left join p10_casee_lawyer_re on p10_casee_lawyer_re.casee_id = p10_casee.casee_id left join p10_court on p10_court.court_id = p10_casee.court_id left join (select * from ( select p10_task_node.target_id,p10_task_node.node_key,p10_task_node.node_id from p10_task_node where p10_task_node.`status` = 0 and p10_task_node.node_attributes = 400 ORDER BY p10_task_node.sort asc )task_node GROUP BY task_node.target_id ) task_node on task_node.target_id = p10_target.target_id where p10_casee.del_flag = 0
and p10_target.goal_type = #{query.goalType}
and task_node.node_key = #{query.nodeKey}
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.executed_name like concat('%', #{query.executedName}, '%')
and p10_project_casee_re.actual_name like concat('%', #{query.actualName}, '%')
and p10_casee.casee_type = #{query.caseeType}
and p10_casee.status = #{query.status}
AND p10_casee.start_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')
ORDER BY p10_casee.status asc,p10_casee.start_time desc
