mapper.select:
select p10_casee.*,p10_court.court_name as courtName,p10_casee_lawyer_re.actual_name as lawyerName,p10_project_casee_re.actual_name as actualName from p10_casee left join p10_court on p10_court.court_id = p10_casee.court_id left join p10_casee_lawyer_re on p10_casee_lawyer_re.casee_id = p10_casee.casee_id left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id where p10_casee.del_flag = 0
and p10_project_casee_re.project_id = #{projectId}
and p10_casee.casee_type in
#{item}
order by p10_casee.casee_type,p10_casee.category,p10_casee.start_time

select p10_casee.* from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id where p10_casee.del_flag = 0
and p10_project_casee_re.project_id = #{projectId}
and p10_casee.status in
#{item}
LIMIT 1

select p10_subject.* from p10_casee_subject_re
left join p10_subject on p10_subject.subject_id = p10_casee_subject_re.subject_id
where p10_casee_subject_re.del_flag = 0
and p10_casee_subject_re.casee_id = #{query.caseeId}

	and p10_casee_subject_re.type = #{query.type}


	and p10_subject.name like concat('%', #{query.subjectName}, '%')
