select DISTINCT p10_project.project_id,p10_project.company_code,p10_casee.*,p10_court.court_name,p10_project_casee_re.actual_name as userName,p10_task_node.node_id from p10_casee left join p10_assets_re on p10_assets_re.casee_id = p10_casee.casee_id left join p10_project on p10_project.project_id = p10_assets_re.project_id left join p10_court on p10_court.court_id = p10_casee.court_id left join p10_target on p10_target.casee_id = p10_casee.casee_id left join p10_task_node on p10_task_node.target_id = p10_target.target_id left join p10_project_casee_re on p10_project_casee_re.project_id = p10_project.project_id and p10_project_casee_re.casee_id = p10_casee.casee_id left join ( select p10_assets_re.* from p10_assets_re left join p10_assets on p10_assets.assets_id = p10_assets_re.assets_id where p10_assets_re.casee_id is not null and if(p10_assets.type=20100,json_extract( p10_assets_re.assets_re_detail, '$.assetsFreeze' ) is null,json_extract( p10_assets_re.assets_re_detail, '$.assetsSeizure') is null) GROUP BY p10_assets_re.casee_id )assets on assets.casee_id = p10_casee.casee_id where p10_project.del_flag = 0 and p10_assets_re.casee_id is not null and p10_project.`status` = 1000 and p10_casee.`status` = 1 and assets.assets_id is null and p10_target.goal_type = 10001 and p10_task_node.node_attributes = 400 and if(casee_type = 1010,p10_task_node.node_key="liQui_SQ_SQBQJG_SQBQJG",p10_task_node.node_key="liQui_SSBQ_SSBQBQJG_SSBQBQJG")
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.company_code like concat('%', #{query.caseeNumber}, '%') or p10_casee.casee_number like concat('%', #{query.caseeNumber}, '%'))
and p10_casee.casee_type = #{query.caseeType}
and p10_subject.name like concat('%', #{query.executedName}, '%')
AND p10_casee.start_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

SELECT (sum(YEAR ( p10_casee.start_time )= YEAR (NOW())) - sum( ( YEAR ( p10_casee.start_time )= YEAR ( NOW())- 1 ) )) FROM p10_casee LEFT JOIN p10_project_casee_re ON p10_casee.casee_id = p10_project_casee_re.casee_id and p10_project_casee_re.del_flag = 0 LEFT JOIN p10_project ON p10_project_casee_re.project_id = p10_project.project_id and p10_project.del_flag = 0 WHERE p10_casee.del_flag = 0 AND (YEAR ( p10_casee.start_time )= YEAR (NOW()) OR YEAR ( p10_casee.start_time )= YEAR (NOW())- 1)
AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}
