mapper.select:
SELECT p10_customer.customer_id, p10_customer.customer_type, p10_subject.name as subjectName, sys_user.actual_name as recommenderName, p10_institution.ins_name, p10_outles.outles_name FROM `p10_customer` LEFT JOIN p10_subject ON p10_customer.subject_id = p10_subject.subject_id AND p10_subject.del_flag = 0 LEFT JOIN sys_user ON p10_customer.recommender_id = sys_user.user_id AND sys_user.del_flag = 0 LEFT JOIN p10_institution ON p10_customer.ins_id = p10_institution.ins_id AND p10_institution.del_flag = 0 LEFT JOIN p10_outles ON p10_customer.outles_id = p10_outles.outles_id AND p10_outles.del_flag = 0 WHERE p10_customer.del_flag = 0
and p10_institution.ins_id = #{query.insId}
and p10_outles.outles_id = #{query.outlesId}
and p10_subject.name like concat('%',#{query.subjectName},'%')
and sys_user.actual_name like concat('%',#{query.recommenderName},'%')
and p10_customer.customer_type = #{query.customerType}
