mapper.select:
select p10_payment_record.*,json_extract(p10_project.project_detail,'$.projectAmount') as projectAmount from p10_payment_record left join p10_project on p10_project.project_id=p10_payment_record.project_id where p10_payment_record.del_flag=0 and ISNULL(p10_payment_record.father_id)
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and p10_payment_record.company_code like concat('%', #{query.companyCode}, '%')
and p10_payment_record.subject_name like concat('%', #{query.subjectName}, '%')
AND p10_payment_record.payment_date BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

select SUM(payment_amount) from p10_payment_record
where del_flag = 0

	and payment_type = #{query.paymentType}


	and status = #{query.status}


	and project_id = #{query.projectId}


	and expense_record_id = #{query.expenseRecordId}

select if(ISNULL(payment_amount),0,SUM(payment_amount)) from p10_payment_record
where del_flag = 0
	and funds_type = #{fundsType}
	and project_id = #{projectId}

		and casee_id = #{caseeId}

SELECT ( (SELECT IF(sum( p10_payment_record.payment_amount ) IS NULL,0,sum( p10_payment_record.payment_amount )) AS thisYear FROM p10_payment_record LEFT JOIN p10_project ON p10_payment_record.project_id = p10_project.project_id and p10_project.del_flag = 0 WHERE p10_payment_record.del_flag = 0
AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}
AND p10_payment_record.father_id IS NOT NULL AND YEAR ( p10_payment_record.payment_date )= YEAR (NOW())) - (SELECT IF(sum( p10_payment_record.payment_amount ) IS NULL,0,sum( p10_payment_record.payment_amount )) AS lastYear FROM p10_payment_record LEFT JOIN p10_project ON p10_payment_record.project_id = p10_project.project_id and p10_project.del_flag = 0 WHERE p10_payment_record.del_flag = 0
AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}
AND p10_payment_record.father_id IS NOT NULL AND YEAR ( p10_payment_record.payment_date )= YEAR (NOW())- 1 ))

SELECT sum( payment_amount ) FROM p10_payment_record LEFT JOIN p10_project ON p10_payment_record.project_id = p10_project.project_id AND p10_project.del_flag = 0 WHERE p10_payment_record.del_flag = 0 AND DATE_FORMAT( p10_payment_record.payment_date, '%Y%m' ) = DATE_FORMAT( CURDATE(), '%Y%m' )
AND p10_project.ins_id = #{insId}
AND p10_project.outles_id = #{outlesId}
AND p10_payment_record.father_id IS NOT NULL

SELECT


		IFNULL( sum(CASE YEAR (p10_payment_record.payment_date) WHEN #{params} THEN p10_payment_record.payment_amount ELSE 0 END), 0) AS #{params}




		IFNULL( sum( CASE DATE_FORMAT( p10_payment_record.payment_date, '%Y-%m' ) WHEN  #{params} THEN p10_payment_record.payment_amount ELSE 0 END ), 0) AS #{params}


FROM
p10_payment_record
LEFT JOIN p10_project ON p10_payment_record.project_id = p10_project.project_id
AND p10_project.del_flag = 0
WHERE
p10_payment_record.del_flag = 0

	AND p10_project.ins_id = #{insId}


	AND p10_project.outles_id = #{outlesId}

AND p10_payment_record.father_id IS NOT NULL

select * from p10_payment_record
where p10_payment_record.del_flag = 0
and p10_payment_record.project_id = #{projectId}
