mapper.select:
select p10_project.*,json_extract(p10_transfer_record.transfer_detail,'$.handoverAmount') as handoverAmount,json_extract(p10_transfer_record.transfer_detail,'$.litigation') as litigation,p10_bank_loan.mortgage_situation from p10_project left join p10_transfer_record on p10_transfer_record.source_id = p10_project.project_id and p10_transfer_record.del_flag = 0 and p10_transfer_record.transfer_type = 0 and p10_transfer_record.status = 1 left join p10_bank_loan on p10_bank_loan.bank_loan_id = p10_transfer_record.source_id and p10_transfer_record.transfer_type = 0 where p10_project.del_flag = 0 and p10_project.project_type = 100
and p10_project.status = #{query.status}
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and p10_project.proposers_names like concat('%', #{query.proposersNames}, '%')
and p10_project.subject_persons like concat('%', #{query.subjectPersons}, '%')
and p10_project.user_nick_name like concat('%', #{query.userNickName}, '%')
and p10_project.company_code like concat('%', #{query.companyCode}, '%')
AND p10_project.take_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')
ORDER BY p10_project.status asc,p10_project.take_time desc

select p10_subject.*,p10_project_subject_re.type from p10_project_subject_re left join p10_subject on p10_subject.subject_id = p10_project_subject_re.subject_id where p10_project_subject_re.del_flag = 0 and p10_project_subject_re.project_id = #{query.projectId}
and p10_project_subject_re.type in
#{item}
and p10_subject.name like concat('%', #{query.subjectName}, '%')
