select p10_project.*,entrustInsOutles.outles_name as entrustOutlesName from p10_project left join p10_institution on p10_institution.ins_id = p10_project.ins_id left join p10_outles on p10_outles.outles_id = p10_project.outles_id left join p10_project_outles_deal_re on p10_project_outles_deal_re.project_id = p10_project.project_id left join p10_outles entrustInsOutles on entrustInsOutles.outles_id = p10_project_outles_deal_re.outles_id where p10_project.del_flag = 0 and p10_project.project_type = 100
and p10_project.status = #{query.status}
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.proposers_names like concat('%', #{query.proposersNames}, '%') or entrustInsOutles.outles_name like concat('%', #{query.proposersNames}, '%'))
and p10_project.subject_persons like concat('%', #{query.subjectPersons}, '%')
and p10_project.user_nick_name like concat('%', #{query.userNickName}, '%')
and p10_project.company_code like concat('%', #{query.companyCode}, '%')
and p10_institution.ins_name like concat('%', #{query.insName}, '%')
and p10_outles.outles_name like concat('%', #{query.outlesName}, '%')
and json_extract(p10_project.project_detail,'$.mortgageSituation') = #{query.mortgageSituation}
AND p10_project.take_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

select sum(json_extract(p10_project.project_detail,'$.principal')) sumPrincipal,sum(json_extract(p10_project.project_detail,'$.interest')) sumPrincipalInterestAmount,sum(json_extract(p10_project.project_detail,'$.principalInterestAmount')) sumProjectAmount from p10_project left join p10_institution on p10_institution.ins_id = p10_project.ins_id left join p10_outles on p10_outles.outles_id = p10_project.outles_id left join p10_project_outles_deal_re on p10_project_outles_deal_re.project_id = p10_project.project_id left join p10_outles entrustInsOutles on entrustInsOutles.outles_id = p10_project_outles_deal_re.outles_id where p10_project.del_flag = 0 and p10_project.project_type = 100
and p10_project.status = #{query.status}
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and (p10_project.proposers_names like concat('%', #{query.proposersNames}, '%') or entrustInsOutles.outles_name like concat('%', #{query.proposersNames}, '%'))
and p10_project.subject_persons like concat('%', #{query.subjectPersons}, '%')
and p10_project.user_nick_name like concat('%', #{query.userNickName}, '%')
and p10_project.company_code like concat('%', #{query.companyCode}, '%')
and p10_institution.ins_name like concat('%', #{query.insName}, '%')
and p10_outles.outles_name like concat('%', #{query.outlesName}, '%')
and json_extract(p10_project.project_detail,'$.mortgageSituation') = #{query.mortgageSituation}
AND p10_project.take_time BETWEEN DATE_FORMAT(#{query.beginDate}, '%Y-%m-%d 00:00:00') and DATE_FORMAT(#{query.endDate}, '%Y-%m-%d 23:59:59')

select p10_subject.*,p10_project_subject_re.type,p10_project_subject_re.subject_re_id, p10_project_subject_re.describes from p10_project_subject_re left join p10_subject on p10_subject.subject_id = p10_project_subject_re.subject_id where p10_project_subject_re.del_flag = 0 and p10_project_subject_re.project_id = #{query.projectId}
