select p10_project.*,json_extract(p10_transfer_record.transfer_detail,'$.litigation') as litigation from p10_project
left join p10_transfer_record on p10_transfer_record.project_id=p10_project.project_id
where p10_project.project_id = #{projectId}

SELECT IFNULL((sum( YEAR ( take_time )= YEAR (NOW())) - sum( ( YEAR ( take_time )= YEAR ( NOW())- 1 ) ) ), 0) FROM p10_project WHERE del_flag = 0 and ( YEAR ( take_time )= YEAR ( NOW()) OR YEAR ( take_time )= YEAR ( NOW())- 1 )
and ins_id = #{insId}
and outles_id = #{outlesId}

select * from p10_project
where del_flag = 0
and project_id = #{projectId}

select DISTINCT p10_project.*,json_extract(p10_transfer_record.transfer_detail,'$.litigation') as litigation from ( select p10_project.* from p10_project left join p10_project_casee_re on p10_project_casee_re.project_id = p10_project.project_id left join p10_transfer_record on p10_transfer_record.project_id = p10_project.project_id left join p10_casee on p10_casee.casee_id = p10_project_casee_re.casee_id and p10_casee.casee_type = 3010 left join (select p10_casee.casee_id from p10_casee left join p10_project_casee_re on p10_project_casee_re.casee_id = p10_casee.casee_id where p10_casee.del_flag = 0 and p10_casee.casee_type in (2020,2021) and p10_casee.`status` = 3 GROUP BY p10_project_casee_re.project_id )casee on casee.casee_id = p10_project_casee_re.casee_id where p10_project.del_flag = 0 and p10_project.project_type = 100 and p10_project.`status` = 1000 and p10_casee.casee_id is null and casee.casee_id is not null and if(p10_casee.casee_type = 2020, json_extract( p10_casee.casee_detail, '$.firstTrialRefereeResult.refereeType' ) != 2, json_extract( p10_casee.casee_detail, '$.secondTrialRefereeResult.refereeType' ) != 2) and json_extract( p10_transfer_record.transfer_detail, '$.litigation' ) = 0 union select p10_project.* from p10_project left join p10_project_casee_re on p10_project_casee_re.project_id = p10_project.project_id left join p10_transfer_record on p10_transfer_record.project_id = p10_project.project_id left join p10_casee on p10_casee.casee_id = p10_project_casee_re.casee_id and p10_casee.casee_type = 3010 where p10_project.del_flag = 0 and p10_project.project_type = 100 and p10_project.`status` = 1000 and p10_casee.casee_id is null and json_extract( p10_transfer_record.transfer_detail, '$.litigation' ) = 1 union select p10_project.* from p10_project left join p10_project_casee_re on p10_project_casee_re.project_id = p10_project.project_id left join p10_casee on p10_casee.casee_id = p10_project_casee_re.casee_id and p10_casee.casee_type = 3010 left join (select reconciliatio_mediation_id,project_id,max(reconciliatio_mediation_data),status from p10_reconciliatio_mediation GROUP BY project_id )p10_reconciliatio_mediation on p10_reconciliatio_mediation.project_id = p10_project.project_id and p10_reconciliatio_mediation.`status` = 3 where p10_project.del_flag = 0 and p10_project.project_type = 100 and p10_project.`status` = 1000 and p10_reconciliatio_mediation.reconciliatio_mediation_id is not null and p10_casee.casee_id is null )p10_project left join p10_transfer_record on p10_transfer_record.project_id = p10_project.project_id
and p10_project.ins_id = #{login.insId}
and p10_project.outles_id = #{login.outlesId}
and p10_project.subject_persons like concat('%', #{query.subjectPersons}, '%')
and p10_project.company_code like concat('%', #{query.companyCode}, '%')

