mapper.select:
select p10_target.*,p10_casee.company_code companyCode, p10_address.information_address, p10_address.province, p10_address.city, p10_address.area from p10_target left join p10_casee on p10_casee.casee_id = p10_target.casee_id left join p10_address on p10_address.address_id=p10_target.address_id and p10_address.del_flag=0 where p10_target.del_flag = 0
and p10_target.casee_id = #{query.caseeId}
and p10_target.business_type = #{query.businessType}
and p10_target.target_category = #{query.targetCategory}
and p10_target.status = #{query.status}
and p10_target.target_name like concat('%', #{query.targetName}, '%')
and p10_target.company_code like concat('%', #{query.companyCode}, '%')
and p10_target.target_type = #{query.targetType}
and (p10_target.create_ins_id=#{query.insId} )
and (p10_target.create_outles_id=#{query.outlesId})
order by p10_casee.year,p10_casee.alias,p10_casee.word,p10_target.create_time desc

mapper.update:
update p10_target SET

	business_data = JSON_SET(business_data, #{params.key}, #{params.value})

where target_id=#{query.targetId};

mapper.select:
SELECT p10_task_node.* FROM p10_target left join p10_task_node on p10_task_node.target_id=p10_target.target_id WHERE p10_task_node.project_id=#{projectId} and p10_target.casee_id=#{caseeId} and p10_target.project_id = #{projectId} AND p10_target.procedure_nature=#{procedureNature} and p10_target.del_flag=0 AND p10_task_node.node_type=0
and p10_target.goal_id=#{id}
ORDER BY p10_task_node.sort asc
