mapper.select:
SELECT p10_task_node.*, p10_casee.company_code AS companyCode, p10_casee.casee_type AS caseeType, p10_target.target_name AS targetName, p10_target.target_type AS targetType, p10_target.status_confirm AS targetStatusConfirm, p10_target.business_type AS businessType, commissionIns.ins_name AS commissionInsName, commissionOutles.outles_name AS commissionOutlesName, commissionUser.nick_name AS commissionNickName, entrustedIns.ins_name AS entrustedInsName, entrustedOutles.outles_name AS entrustedOutlesName, entrustedUser.nick_name AS entrustedNickName, p10_task_record.status as taskRecordStatus, p10_task_record.trustee_type as recordTrusteeType, p10_task_record.trustee_status as recordTrusteeStatus, p10_task_record.create_time as taskRecordCreateTime, p10_task_record.update_time as taskRecordUpdateTime FROM p10_task_record LEFT JOIN p10_casee ON p10_casee.casee_id = p10_task_record.casee_id AND p10_casee.del_flag = 0 LEFT JOIN p10_target ON p10_target.target_id = p10_task_record.target_id AND p10_target.del_flag = 0 LEFT JOIN p10_task_node ON p10_task_node.node_id = p10_task_record.node_id AND p10_task_node.del_flag = 0 LEFT JOIN p10_institution AS commissionIns ON commissionIns.ins_id = p10_task_record.submit_ins_id AND commissionIns.del_flag = 0 LEFT JOIN p10_outles AS commissionOutles ON commissionOutles.outles_id = p10_task_record.submit_outles_id AND commissionOutles.del_flag = 0 LEFT JOIN sys_user AS commissionUser ON commissionUser.user_id = p10_task_record.submit_id AND commissionUser.del_flag = 0 LEFT JOIN p10_institution AS entrustedIns ON entrustedIns.ins_id = p10_task_record.trustee_ins_id AND entrustedIns.del_flag = 0 LEFT JOIN p10_outles AS entrustedOutles ON entrustedOutles.outles_id = p10_task_record.trustee_outles_id AND entrustedOutles.del_flag = 0 LEFT JOIN sys_user AS entrustedUser ON entrustedUser.user_id = p10_task_record.trustee_id AND entrustedUser.del_flag = 0 LEFT JOIN act_hi_procinst ON act_hi_procinst.BUSINESS_KEY_ = p10_task_record.node_id LEFT JOIN act_ru_task ON act_ru_task.PROC_INST_ID_ = act_hi_procinst.PROC_INST_ID_
WHERE p10_task_record.del_flag=0 AND p10_task_record.status=101 AND p10_task_record.auditor_id=#{userId} AND act_ru_task.ASSIGNEE_=#{userId}
WHERE p10_task_record.del_flag=0 AND p10_task_record.can_entrust=1 AND act_ru_task.ASSIGNEE_=#{userId} AND p10_task_record.trustee_id=#{userId} AND ISNULL(p10_task_record.handler_id)
and p10_task_node.node_name = #{taskFlowDTO.nodeName}
and p10_task_node.ins_type = #{taskFlowDTO.insType}
AND p10_task_node.update_time BETWEEN DATE_FORMAT(#{taskFlowDTO.beginDate} , '%Y-%m-%d 00:00:00')and DATE_FORMAT(#{taskFlowDTO.endDate}, '%Y-%m-%d 23:59:59')
and p10_casee.company_code like concat('%', #{taskFlowDTO.companyCode}, '%')
and p10_target.target_name like concat('%', #{taskFlowDTO.targetName}, '%')
ORDER BY p10_task_node.update_time desc

SELECT * FROM p10_task_node WHERE node_key=#{taskNodeKey}

	and project_id=#{taskNode.projectId}


	and casee_id=#{taskNode.caseeId}


	and target_id=#{taskNode.targetId}

AND del_flag=0 AND node_type=0
ORDER BY p10_task_node.create_time desc LIMIT 1
