mapper.select:
SELECT *, entrustedInstitution.ins_name AS entrustedInsName, entrustedOutles.outles_name AS entrustedOutlesName, institution.ins_name AS insName, outles.outles_name AS outlesName, p10_bank_loan.* FROM p10_transfer_record LEFT JOIN p10_institution AS entrustedInstitution ON entrustedInstitution.ins_id = p10_transfer_record.entrusted_ins_id LEFT JOIN p10_outles AS entrustedOutles ON entrustedOutles.outles_id = p10_transfer_record.entrusted_outles_id LEFT JOIN p10_bank_loan ON p10_bank_loan.bank_loan_id = p10_transfer_record.source_id and transfer_type=0 LEFT JOIN p10_institution AS institution ON institution.ins_id = p10_bank_loan.ins_id LEFT JOIN p10_outles AS outles ON outles.outles_id = p10_bank_loan.outles_id WHERE p10_transfer_record.del_flag = 0
and p10_transfer_record.entrusted_ins_id = #{login.insId}
and p10_transfer_record.entrusted_outles_id = #{login.outlesId}
and p10_transfer_record.status=#{query.status}
and p10_transfer_record.handover_time=#{query.handoverTime}
and p10_bank_loan.subject_name like concat('%', #{query.subjectName}, '%')
ORDER BY p10_transfer_record.create_time desc

SELECT p10_transfer_record.*, institution.ins_name AS insName, outles.outles_name AS outlesName, p10_bank_loan.* FROM p10_transfer_record LEFT JOIN p10_bank_loan ON p10_bank_loan.bank_loan_id = p10_transfer_record.source_id and transfer_type=0 LEFT JOIN p10_institution AS institution ON institution.ins_id = p10_bank_loan.ins_id LEFT JOIN p10_outles AS outles ON outles.outles_id = p10_bank_loan.outles_id WHERE p10_transfer_record.del_flag = 0
and p10_transfer_record.transfer_record_id=#{transferRecordId}
and p10_transfer_record.project_id=#{projectId}
LIMIT 1

select * from p10_transfer_record
where del_flag = 0 and project_id = #{projectId}

	and transfer_type = #{transferType}
